import React, { useState } from "react";
import emailjs from "emailjs-com";
import logo from "./assets/logo.png";
import qr from "./assets/qr.png";
import successImg from "./assets/success.png";

function App() {
  const [formData, setFormData] = useState({
    name: "",
    occupation: "",
    email: "",
    whatsapp: "",
    problem: "",
    preferredTime: "",
    screenshot: null,
  });

  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: files ? files[0] : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    let base64Image = "";
    if (formData.screenshot) {
      const reader = new FileReader();
      reader.readAsDataURL(formData.screenshot);
      base64Image = await new Promise((resolve) => {
        reader.onload = () => resolve(reader.result);
      });
    }

    emailjs.send("service_ted5m2h", "template_5qmfosa", {
      name: formData.name,
      email: formData.email,
    }, "D8Cr-U9g1MVqsc5bv");

    emailjs.send("service_ted5m2h", "template_nzvxr9o", {
      name: formData.name,
      email: formData.email,
      occupation: formData.occupation,
      whatsapp: formData.whatsapp,
      problem: formData.problem,
      preferredTime: formData.preferredTime,
      screenshot: base64Image,
    }, "D8Cr-U9g1MVqsc5bv");

    setSubmitted(true);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#0b0c2a] via-[#111340] to-[#1a1c3c] text-white font-sans">
      {/* Header */}
      <header className="sticky top-0 z-50 bg-[#101225] bg-opacity-90 shadow px-6 py-4 flex justify-between items-center">
        <div className="flex items-center gap-3">
          <img src={logo} alt="Logo" className="w-10 h-10" />
          <div>
            <h1 className="text-xl font-bold text-blue-300">Ghar-ki-Baat</h1>
            <p className="text-sm text-gray-300">Where Your Mind Finds Peace</p>
          </div>
        </div>
        <nav className="hidden md:flex gap-6 text-blue-200 font-medium">
          <a href="#home" className="hover:text-white">Home</a>
          <a href="#book" className="hover:text-white">Book</a>
          <a href="#contact" className="hover:text-white">Contact</a>
        </nav>
      </header>

      {/* Hero */}
      <section className="py-20 px-6 md:px-20 bg-[url('https://www.transparenttextures.com/patterns/stardust.png')] bg-repeat">
        <div className="max-w-6xl mx-auto flex flex-col md:flex-row items-center justify-between gap-8">
          <div className="md:w-1/2 mb-10 md:mb-0">
            <h2 className="text-4xl font-bold text-blue-300 mb-4">SafeCircle: A Place to Breathe</h2>
            <p className="text-lg text-gray-300">Talk about anything — life, stress, or silence. We listen with care, not judgment. Book a session and feel lighter.</p>
          </div>
          <div className="md:w-1/3">
            <img src={logo} alt="Hero Logo" className="w-48 h-48 animate-pulse drop-shadow" />
          </div>
        </div>
      </section>

      {/* What We Do */}
      <section id="home" className="p-10 max-w-4xl mx-auto text-center bg-[#181a38] rounded-xl shadow-lg mt-10">
        <h2 className="text-2xl font-bold text-blue-300 mb-6">What We Do</h2>
        <ul className="space-y-3 text-lg text-gray-200">
          <li>🌙 Safe space to talk — freely, fearlessly.</li>
          <li>🎧 Calm, one-on-one conversations — no pressure.</li>
          <li>🌌 Feel lighter, gain clarity, find inner calm.</li>
        </ul>
      </section>

      {/* Book Session */}
      <section id="book" className="px-6 py-16 max-w-4xl mx-auto">
        <h2 className="text-3xl font-bold text-center text-blue-300 mb-10">Book a Session</h2>
        {submitted ? (
          <div className="text-center">
            <img src={successImg} alt="Success" className="w-32 mx-auto mb-4" />
            <p className="text-lg text-green-300">Thank you! We will reach out shortly.</p>
          </div>
        ) : (
          <form onSubmit={handleSubmit} className="space-y-5 bg-[#181a38] p-8 rounded-xl shadow-lg">
            <input type="text" name="name" placeholder="Your Name" value={formData.name} onChange={handleChange} className="w-full p-3 rounded bg-[#1f213f] text-white" required />
            <select name="occupation" value={formData.occupation} onChange={handleChange} className="w-full p-3 rounded bg-[#1f213f] text-white" required>
              <option value="">Select Occupation</option>
              <option value="Student">Student</option>
              <option value="Working Professional">Working Professional</option>
              <option value="Others">Others</option>
            </select>
            <input type="email" name="email" placeholder="Email ID" value={formData.email} onChange={handleChange} className="w-full p-3 rounded bg-[#1f213f] text-white" required />
            <input type="text" name="whatsapp" placeholder="WhatsApp Number" value={formData.whatsapp} onChange={handleChange} className="w-full p-3 rounded bg-[#1f213f] text-white" required />
            <textarea name="problem" placeholder="Describe your concern..." value={formData.problem} onChange={handleChange} className="w-full p-3 rounded bg-[#1f213f] text-white" required />
            <input type="text" name="preferredTime" placeholder="Preferred Date/Time" value={formData.preferredTime} onChange={handleChange} className="w-full p-3 rounded bg-[#1f213f] text-white" required />
            <input type="file" name="screenshot" accept="image/*" onChange={handleChange} className="text-sm text-gray-400" />
            <p className="text-sm text-gray-400">Upload payment confirmation screenshot</p>
            <button type="submit" className="w-full bg-blue-500 hover:bg-blue-600 text-white p-3 rounded mt-4">Submit</button>
            <p className="text-sm text-gray-400 text-center mt-2">Our team will contact you after confirming your payment.</p>
          </form>
        )}
      </section>

      {/* Testimonials */}
      <section className="px-6 py-16 bg-[#151731]">
        <h2 className="text-3xl font-bold text-blue-300 text-center mb-10">What Our Users Say</h2>
        <div className="grid md:grid-cols-2 gap-6 max-w-5xl mx-auto">
          {[
            { name: "Ananya", quote: "Felt so light after the session. Thank you for truly listening." },
            { name: "Ravi", quote: "This space gave me peace I hadn’t felt in years." },
            { name: "Megha", quote: "No pressure, just listening — that's all I needed." },
            { name: "Tarun", quote: "So calming and helpful. Highly recommended." },
          ].map((t, i) => (
            <div key={i} className="bg-[#1c1f3d] p-6 rounded-xl shadow text-white">
              <p className="text-gray-300 italic">“{t.quote}”</p>
              <p className="mt-2 font-semibold text-blue-200">— {t.name}</p>
            </div>
          ))}
        </div>
      </section>

      {/* FAQs */}
      <section className="px-6 py-16 max-w-5xl mx-auto">
        <h2 className="text-3xl font-bold text-center text-blue-300 mb-10">FAQs</h2>
        <div className="space-y-6">
          <div>
            <h4 className="font-semibold text-blue-200">Is this a therapy session?</h4>
            <p className="text-gray-300">No, we provide listening sessions — not medical or therapeutic advice.</p>
          </div>
          <div>
            <h4 className="font-semibold text-blue-200">Is my data private?</h4>
            <p className="text-gray-300">Absolutely. We do not share or record any conversations.</p>
          </div>
          <div>
            <h4 className="font-semibold text-blue-200">How do I pay?</h4>
            <p className="text-gray-300">Scan the QR code below and upload your payment screenshot while booking.</p>
            <img src={qr} alt="Payment QR" className="w-32 mt-2" />
          </div>
        </div>
      </section>

      {/* CTA */}
      <section id="contact" className="text-center py-12 bg-[#0f102a]">
        <h2 className="text-3xl font-bold text-blue-300 mb-4">Need Help?</h2>
        <p className="text-gray-300 mb-6">Reach out via email for any queries or custom requests.</p>
        <a href="mailto:gharkibaat@support.com" className="inline-block bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded">Email Support</a>
      </section>

      {/* Footer */}
      <footer className="text-center py-6 text-gray-500 bg-[#0d0e24] text-sm">
        © 2025 SafeCircle. All rights reserved.
      </footer>
    </div>
  );
}

export default App;
