import React, { useState } from "react";
import emailjs from "emailjs-com";
import { motion } from "framer-motion";
import logo from "./assets/logo.png";
import successImage from "./assets/success.png";
import qr from "./assets/qr.png";

function App() {
  const [formData, setFormData] = useState({ name: "", email: "", image: null });
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();

    emailjs
      .send(
        "service_ted5m2h",
        "template_5qmfosa",
        {
          name: formData.name,
          email: formData.email,
        },
        "D8Cr-U9g1MVqsc5bv"
      )
      .then(() => console.log("Confirmation sent"));

    emailjs
      .send(
        "service_ted5m2h",
        "template_nzvxr9o",
        {
          name: formData.name,
          email: formData.email,
        },
        "D8Cr-U9g1MVqsc5bv"
      )
      .then(() => console.log("Alert sent to admin"));

    setSubmitted(true);
  };

  return (
    <div className="bg-gradient-to-b from-[#e6f5f2] to-white text-gray-800 font-sans scroll-smooth">
      {/* --- HEADER --- */}
      <header className="flex justify-between items-center px-6 py-4 shadow-md bg-white">
        <div>
          <h1 className="text-2xl font-bold text-teal-700">Ghar-ki-Baat</h1>
          <p className="text-sm text-gray-600 italic">When no one listens, we do.</p>
        </div>
        <img src={logo} alt="Logo" className="w-16 h-16 rounded-full shadow" />
      </header>

      {/* --- HERO --- */}
      <section className="py-12 px-6 bg-gradient-to-r from-[#ecfdf5] to-white">
        <div className="flex flex-col md:flex-row items-center justify-between max-w-6xl mx-auto">
          <div className="text-center md:text-left mb-6 md:mb-0">
            <motion.h2
              initial={{ opacity: 0, y: -30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 1 }}
              className="text-4xl font-semibold text-teal-800 mb-3"
            >
              Ghar-ki-Baat
            </motion.h2>
            <p className="text-gray-700 text-lg">A space for real talk ‚Äî more than advice, lighter than therapy.</p>
          </div>
          <img src={logo} alt="Logo" className="w-24 h-24 rounded-full shadow-md" />
        </div>
