import React, { useState } from "react";
import emailjs from "emailjs-com";
import logo from "./assets/logo.png";
import qr from "./assets/qr.png";
import successImg from "./assets/success.png";

function App() {
  const [formData, setFormData] = useState({
    name: "",
    occupation: "",
    email: "",
    whatsapp: "",
    problem: "",
    preferredTime: "",
    screenshot: null,
  });

  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: files ? files[0] : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    let base64Image = "";
    if (formData.screenshot) {
      const reader = new FileReader();
      reader.readAsDataURL(formData.screenshot);
      base64Image = await new Promise((resolve) => {
        reader.onload = () => resolve(reader.result);
      });
    }

    // First mail (basic)
    emailjs.send(
      "service_ted5m2h",
      "template_5qmfosa",
      {
        name: formData.name,
        email: formData.email,
      },
      "D8Cr-U9g1MVqsc5bv"
    );

    // Second mail (detailed)
    emailjs.send(
      "service_ted5m2h",
      "template_nzvxr9o",
      {
        name: formData.name,
        email: formData.email,
        occupation: formData.occupation,
        whatsapp: formData.whatsapp,
        problem: formData.problem,
        preferredTime: formData.preferredTime,
        screenshot: base64Image,
      },
      "D8Cr-U9g1MVqsc5bv"
    );

    setSubmitted(true);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#0b0c2a] via-[#111340] to-[#1a1c3c] text-white font-sans">
      {/* Header */}
      <header className="sticky top-0 z-50 bg-[#101225] bg-opacity-90 shadow px-6 py-4 flex justify-between items-center">
        <div className="flex items-center gap-3">
          <img src={logo} alt="Logo" className="w-10 h-10 rounded-full" />
          <div>
            <h1 className="text-xl font-bold text-blue-300">Ghar-ki-Baat</h1>
            <p className="text-sm text-gray-300">Where Your Mind Finds Peace</p>
          </div>
        </div>
        <nav className="hidden md:flex gap-6 text-blue-200 font-medium">
          <a href="#home" className="hover:text-white">Home</a>
          <a href="#book" className="hover:text-white">Book</a>
          <a href="#contact" className="hover:text-white">Contact</a>
        </nav>
      </header>

      {/* Hero Section */}
      <section className="py-20 px-6 md:px-20 bg-[url('https://www.transparenttextures.com/patterns/stardust.png')] bg-repeat">
        <div className="max-w-6xl mx-auto flex flex-col md:flex-row items-center justify-between gap-10">
          <div className="md:w-1/2">
            <h2 className="text-4xl font-bold text-blue-300 mb-4">SafeCircle: A Place to Breathe</h2>
            <p className="text-lg text-gray-300">Talk about anything â€” life, stress, or silence. We listen with care, not judgment. Book a session and feel lighter.</p>
          </div>
          <div className="md:w-1/3">
            <img src={logo} alt="Hero Logo" className="w-48 h-48 animate-pulse drop-shadow" />
          </div>
        </div>
      </section>

      {/* What We Do */}
      <section id="home" className="p-10 max-w-4xl mx-auto text-center bg-[#181a38] rounded-xl shadow-lg mt-10">
        <h2 className="text-2xl font-bold text-blue-300 mb-6">What We Do</h2>
        <ul className="space-y-3 text-lg text-gray-200">
          <li>ðŸŒ™ Safe space to talk â€” freely, fearlessly.</li>
          <li>ðŸŽ§ Calm, one-on-one conversations â€” no pressure.</li>
          <li>ðŸŒŒ Feel lighter, gain clarity, find inner calm.</li>
        </ul>
      </section>

      {/* Booking Form */}
      <section id="book" className="p-10 max-w-3xl mx-auto bg-[#14162e] rounded-xl shadow mt-14">
        <h2 className="text-2xl font-bold text-center text-blue-300 mb-6">Book a Session</h2>
        {submitted ? (
          <div className="text-center text-green-400">
            <img src={successImg} alt="Success" className="mx-auto mb-4 w-24 h-24" />
            Your session request has been received. Our team will contact you after payment confirmation.
          </div>
        ) : (
          <form onSubmit={handleSubmit} className="space-y-4 text-white">
            <input type="text" name="name" placeholder="Your Name" required onChange={handleChange} className="w-full p-2 rounded bg-[#1c1e3c] text-white" />
            <select name="occupation" required onChange={handleChange} className="w-full p-2 rounded bg-[#1c1e3c] text-white">
              <option value="">Select Occupation</option>
              <option value="Student">Student</option>
              <option value="Working Professional">Working Professional</option>
              <option value="Other">Other</option>
            </select>
            <input type="email" name="email" placeholder="Email ID" required onChange={handleChange} className="w-full p-2 rounded bg-[#1c1e3c] text-white" />
            <input type="text" name="whatsapp" placeholder="WhatsApp Number" required onChange={handleChange} className="w-full p-2 rounded bg-[#1c1e3c] text-white" />
            <input type="text" name="preferredTime" placeholder="Preferred Date & Time" required onChange={handleChange} className="w-full p-2 rounded bg-[#1c1e3c] text-white" />
            <textarea name="problem" placeholder="Brief About the Issue" required onChange={handleChange} className="w-full p-2 rounded bg-[#1c1e3c] text-white" />
            <input type="file" name="screenshot" accept="image/*" onChange={handleChange} className="w-full text-sm text-gray-400" />
            <p className="text-sm text-blue-200 mt-1">Upload screenshot of payment confirmation.</p>
            <button type="submit" className="w-full bg-blue-600 hover:bg-blue-700 text-white py-2 rounded font-semibold">Submit</button>
            <p className="text-xs text-gray-300 text-center mt-2">Our team will contact you after payment confirmation.</p>
          </form>
        )}
      </section>

      {/* QR Code */}
      <section className="mt-14 text-center">
        <h3 className="text-xl font-bold text-blue-300 mb-4">Scan to Pay</h3>
        <img src={qr} alt="QR Code" className="mx-auto w-48 h-48" />
      </section>

      {/* Footer */}
      <footer id="contact" className="mt-20 bg-[#101225] py-6 text-center text-gray-400 text-sm">
        &copy; 2025 Ghar-ki-Baat | All rights reserved.
      </footer>
    </div>
  );
}

export default App;
