import React, { useState } from "react";
import emailjs from "emailjs-com";
import logo from "./assets/logo.png";
import qr from "./assets/qr.png";
import success from "./assets/success.png";

function App() {
  const [formData, setFormData] = useState({
    name: "",
    age: "",
    whatsapp: "",
    email: "",
    topic: "",
    datetime: "",
  });

  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // 1️⃣ Send confirmation to user
    emailjs
      .send(
        "service_ted5m2h",
        "template_5qmfosa",
        {
          name: formData.name,
          email: formData.email,
        },
        "D8Cr-U9g1MVqsc5bv"
      )
      .then(() => console.log("Confirmation sent"));

    // 2️⃣ Alert admin
    emailjs
      .send(
        "service_ted5m2h",
        "template_nzvxr9o",
        {
          name: formData.name,
          email: formData.email,
        },
        "D8Cr-U9g1MVqsc5bv"
      )
      .then(() => console.log("Alert sent to admin"));

    setSubmitted(true);
  };

  return (
    <div className="font-sans text-white bg-gradient-to-b from-[#1e293b] to-[#0f172a] min-h-screen">
      {/* Hero */}
      <header className="flex items-center justify-between p-6 bg-[#0f172a] shadow-lg">
        <div>
          <h1 className="text-3xl font-bold text-teal-300">SafeCircle</h1>
          <p className="text-sm text-gray-400">Feel heard. Feel lighter.</p>
        </div>
        <img src={logo} alt="Logo" className="h-16 w-16" />
      </header>

      {/* What We Do */}
      <section className="p-6 max-w-4xl mx-auto">
        <h2 className="text-2xl font-semibold text-teal-200 mb-4">What We Do</h2>
        <ul className="space-y-2 text-gray-200 list-disc list-inside">
          <li>Listen to anything on your mind — school, life, overthinking, or just silence.</li>
          <li>Make you feel lighter with calm, honest conversation.</li>
          <li>Give simple guidance or just a space to breathe — no pressure.</li>
        </ul>
      </section>

      {/* Why Trust Us */}
      <section className="p-6 max-w-4xl mx-auto">
        <h2 className="text-2xl font-semibold text-teal-200 mb-4">Why Trust Us?</h2>
        <ul className="space-y-2 text-gray-200 list-disc list-inside">
          <li>100% privacy</li>
          <li>Compassionate listeners</li>
          <li>Real talk</li>
        </ul>
      </section>

      {/* Our Plans */}
      <section className="p-6 max-w-4xl mx-auto">
        <h2 className="text-2xl font-semibold text-teal-200 mb-4">Our Plans</h2>
        <p className="text-gray-300">Choose flexible session lengths and affordable prices designed for students.</p>
      </section>

      {/* Booking Form */}
      <section className="p-6 max-w-5xl mx-auto flex flex-col md:flex-row gap-8 items-center">
        <div className="w-full md:w-1/2">
          <h2 className="text-2xl font-semibold text-teal-200 mb-4">Book a Session</h2>
          {submitted ? (
            <div className="text-center">
              <img src={success} alt="Success" className="w-20 mx-auto mb-4" />
              <p className="text-green-300">Booking successful. We'll reach out soon!</p>
            </div>
          ) : (
            <form onSubmit={handleSubmit} className="space-y-4">
              <input name="name" placeholder="Name" onChange={handleChange} required className="w-full p-2 rounded bg-gray-800 text-white" />
              <input name="age" placeholder="Age" onChange={handleChange} required className="w-full p-2 rounded bg-gray-800 text-white" />
              <input name="whatsapp" placeholder="WhatsApp Number" onChange={handleChange} required className="w-full p-2 rounded bg-gray-800 text-white" />
              <input name="email" placeholder="Email" onChange={handleChange} required className="w-full p-2 rounded bg-gray-800 text-white" />
              <input name="topic" placeholder="Topic / Problem" onChange={handleChange} required className="w-full p-2 rounded bg-gray-800 text-white" />
              <input name="datetime" placeholder="Preferred Date & Time" onChange={handleChange} required className="w-full p-2 rounded bg-gray-800 text-white" />
              <button type="submit" className="w-full bg-teal-500 hover:bg-teal-600 text-white py-2 rounded">Submit</button>
            </form>
          )}
        </div>
        <div className="w-full md:w-1/2 flex justify-center">
          <img src={qr} alt="QR Code" className="w-40 md:w-60" />
        </div>
      </section>

      {/* Testimonials */}
      <section className="p-6 max-w-4xl mx-auto">
        <h2 className="text-2xl font-semibold text-teal-200 mb-4">What Others Say</h2>
        <div className="space-y-4 text-gray-300">
          <p>"I felt genuinely heard. Thank you for this space."</p>
          <p>"It helped me more than I expected. I really needed this."</p>
          <p>"I had no one to talk to. This changed everything for me."</p>
        </div>
      </section>

      {/* FAQ */}
      <section className="p-6 max-w-4xl mx-auto">
        <h2 className="text-2xl font-semibold text-teal-200 mb-4">Frequently Asked Questions</h2>
        <div className="space-y-4 text-gray-300">
          <div>
            <strong>Is this confidential?</strong>
            <p>Yes, 100%. We never store your personal information or chats.</p>
          </div>
          <div>
            <strong>Who are the listeners?</strong>
            <p>Compassionate students and trained peer listeners.</p>
          </div>
          <div>
            <strong>Is it free?</strong>
            <p>Basic sessions are free. Extended ones are minimal cost.</p>
          </div>
        </div>
      </section>

      {/* CTA */}
      <section className="p-6 max-w-4xl mx-auto text-center">
        <h2 className="text-2xl font-semibold text-teal-200 mb-4">Still have questions?</h2>
        <p className="text-gray-300 mb-4">Reach out anytime — we're here for you.</p>
        <div className="flex justify-center gap-4">
          <a href="mailto:safecircle@example.com" className="bg-blue-500 hover:bg-blue-600 px-4 py-2 rounded text-white">Email Us</a>
          <a href="https://wa.me/919000000000" className="bg-green-500 hover:bg-green-600 px-4 py-2 rounded text-white">WhatsApp</a>
        </div>
      </section>

      {/* Footer */}
      <footer className="p-6 text-center text-gray-500 text-sm border-t border-gray-700">
        &copy; 2025 SafeCircle. All rights reserved.
      </footer>
    </div>
  );
}

export default App;
