import React, { useRef } from 'react';
import './App.css';
import emailjs from '@emailjs/browser';
import logo from './assets/logo.png';
import qr from './assets/qr.png';

function App() {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm('service_ted5m2h', 'template_5qmfosa', form.current, 'D8Cr-U9g1MVqsc5bv')
      .then(
        (result) => {
          alert('Booking successful! Please check your email.');
        },
        (error) => {
          alert('Something went wrong. Please try again.');
        }
      );

    emailjs.send(
      'service_ted5m2h',
      'template_nzvxr9o',
      {
        from_name: form.current.user_name.value,
        whatsapp: form.current.whatsapp.value,
        topic: form.current.topic.value,
        occupation: form.current.occupation.value,
        preferred: form.current.preferred.value
      },
      'D8Cr-U9g1MVqsc5bv'
    );
  };

  return (
    <div className="bg-white font-[Poppins]">
      {/* Header */}
      <header className="w-full flex justify-between items-center px-6 py-4 shadow-md bg-white fixed top-0 z-50">
        <div className="text-xl font-bold text-teal-700">Ghar ki Baat</div>
        <nav className="space-x-4 hidden md:block">
          <a href="#home" className="text-gray-700 hover:text-teal-600">Home</a>
          <a href="#about" className="text-gray-700 hover:text-teal-600">About</a>
          <a href="#book" className="text-gray-700 hover:text-teal-600">Book Session</a>
          <a href="#contact" className="text-gray-700 hover:text-teal-600">Contact</a>
        </nav>
      </header>

      {/* Hero Section */}
      <section id="home" className="h-screen flex flex-col md:flex-row justify-between items-center px-6 pt-24 pb-12 bg-gradient-to-br from-blue-50 to-white">
        <div className="max-w-xl">
          <h1 className="text-4xl md:text-5xl font-bold text-teal-800 mb-4">When no one listens, we do.</h1>
          <p className="text-lg text-gray-700 mb-6">Private, safe consultations you can trust.</p>
          <a href="#book" className="bg-teal-600 text-white px-6 py-3 rounded-full shadow hover:bg-teal-700 transition">Book Now</a>
        </div>
        <div className="w-64 h-64 mt-10 md:mt-0 animate-float">
          <img src={logo} alt="Logo" className="w-full h-full object-contain" />
        </div>
      </section>

      {/* Why Trust Us */}
      <section id="about" className="px-6 py-16 text-center bg-white">
        <h2 className="text-3xl font-semibold text-teal-700 mb-10">Why Trust Us</h2>
        <div className="grid md:grid-cols-3 gap-8">
          {["Confidential", "Affordable", "Anonymous Support"].map((item, index) => (
            <div key={index} className="p-6 bg-blue-50 rounded-xl shadow hover:shadow-lg transition">
              <h3 className="text-xl font-medium text-gray-800">{item}</h3>
            </div>
          ))}
        </div>
      </section>

      {/* Testimonials */}
      <section className="px-6 py-16 bg-gray-50">
        <h2 className="text-3xl font-semibold text-teal-700 mb-10 text-center">What People Say</h2>
        <div className="grid md:grid-cols-3 gap-6">
          {[1, 2, 3].map((i) => (
            <div key={i} className="bg-white p-6 rounded-lg shadow">
              <p className="text-gray-600 italic">"Really helped me in tough times. Totally safe space."</p>
              <p className="text-sm text-gray-400 mt-4">— Anonymous User</p>
            </div>
          ))}
        </div>
      </section>

      {/* CTA */}
      <section className="px-6 py-16 bg-teal-600 text-white text-center">
        <h2 className="text-3xl font-bold mb-4">Ready to talk?</h2>
        <p className="mb-6">Click below to begin your private session.</p>
        <a href="#book" className="bg-white text-teal-700 px-6 py-3 rounded-full shadow hover:bg-gray-100">Book a Session</a>
      </section>

      {/* Book Session */}
      <section id="book" className="px-6 py-16 bg-white">
        <h2 className="text-3xl font-semibold text-teal-700 mb-10 text-center">Book a Private Session</h2>
        <div className="grid md:grid-cols-2 gap-10">
          <form ref={form} onSubmit={sendEmail} className="space-y-4">
            <input type="text" name="user_name" placeholder="Full Name" className="w-full border p-3 rounded" required />
            <input type="number" name="age" placeholder="Age" className="w-full border p-3 rounded" required />
            <select name="occupation" className="w-full border p-3 rounded" required>
              <option>Current Occupation</option>
              <option>Student</option>
              <option>Working Professional</option>
              <option>Homemaker</option>
              <option>Other</option>
            </select>
            <input type="text" name="whatsapp" placeholder="WhatsApp Number" className="w-full border p-3 rounded" required />
            <input type="text" name="topic" placeholder="Topic / Problem" className="w-full border p-3 rounded" required />
            <textarea name="preferred" placeholder="Preferred Dates & Times (e.g. Aug 3, 10am & 5pm)" className="w-full border p-3 rounded" required></textarea>
            <input type="file" className="w-full border p-3 rounded" accept="image/*" />
            <p className="text-sm text-gray-600">📩 You will get your session details on WhatsApp within a few hours.</p>
            <button type="submit" className="bg-teal-600 text-white px-6 py-3 rounded hover:bg-teal-700">Submit</button>
          </form>
          <div className="flex flex-col items-center justify-center border p-6 rounded-xl shadow">
            <h3 className="text-lg font-medium text-gray-700 mb-4">Scan to Pay</h3>
            <img src={qr} alt="QR Code" className="w-48 h-48 object-contain" />
          </div>
        </div>
      </section>

      {/* Floating WhatsApp Button */}
      <a
        href="https://wa.me/919999999999?text=Hi,%20I%20want%20to%20book%20a%20session%20with%20Ghar%20ki%20Baat"
        className="fixed bottom-6 right-6 bg-green-500 text-white px-4 py-3 rounded-full shadow-lg hover:bg-green-600 transition"
        target="_blank" rel="noreferrer"
      >
        Chat on WhatsApp
      </a>

      {/* Footer */}
      <footer className="px-6 py-6 text-center text-gray-600 bg-gray-100 mt-12">
        <p>© 2025 Ghar ki Baat. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
