import React, { useState } from "react";
import emailjs from "emailjs-com";
import { motion } from "framer-motion";
import "./App.css";
import logo from './assets/logo.png';
import successImg from './assets/success.png';
import qr from './assets/qr.png';

function App() {
  const [formData, setFormData] = useState({ name: "", email: "", message: "" });
  const [formSubmitted, setFormSubmitted] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const sendEmail = (e) => {
    e.preventDefault();

    // 1️⃣ Send confirmation to user
    emailjs.send(
      "service_ted5m2h",          // ✅ Replace with your EmailJS service ID
      "template_5qmfosa",         // ✅ Template ID for user confirmation
      {
        name: formData.name,
        email: formData.email,
      },
      "D8Cr-U9g1MVqsc5bv"         // ✅ Replace with your EmailJS public key
    ).then(() => {
      console.log("Confirmation sent to user");
    });

    // 2️⃣ Send notification to ADMIN
    emailjs.send(
      "service_ted5m2h",          // ✅ Same service ID
      "template_nzvxr9o",         // ✅ Template ID for admin alert
      {
        name: formData.name,
        email: formData.email,
        message: formData.message,
      },
      "D8Cr-U9g1MVqsc5bv"
    ).then(() => {
      console.log("Notification sent to admin");
      setFormSubmitted(true);
    });
  };

  return (
    <div className="bg-gradient-to-b from-teal-100 to-white min-h-screen p-6 font-sans">
      <header className="flex justify-between items-center mb-8">
        <div>
          <h1 className="text-2xl font-bold text-teal-800">SafeCircle</h1>
          <p className="text-sm italic text-teal-600">When no one listens, we do</p>
        </div>
        <img src={logo} alt="Logo" className="w-16 h-16" />
      </header>

      <div className="bg-white p-6 rounded-xl shadow-xl max-w-xl mx-auto">
        {formSubmitted ? (
          <div className="text-center">
            <img src={successImg} alt="Success" className="w-32 mx-auto mb-4" />
            <h2 className="text-xl font-semibold text-green-700">Booking Successful!</h2>
            <p className="text-gray-600 mt-2">We have received your details. We'll reach out soon.</p>
          </div>
        ) : (
          <form onSubmit={sendEmail} className="space-y-4">
            <h2 className="text-lg font-semibold text-teal-700">Book a Session</h2>
            <p className="text-sm text-gray-500">Pricing: ₹99 / ₹199 / ₹299</p>

            <input
              type="text"
              name="name"
              placeholder="Your Name"
              value={formData.name}
              onChange={handleChange}
              className="w-full p-3 border border-gray-300 rounded"
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Your Email"
              value={formData.email}
              onChange={handleChange}
              className="w-full p-3 border border-gray-300 rounded"
              required
            />
            <textarea
              name="message"
              placeholder="Briefly explain your issue"
              value={formData.message}
              onChange={handleChange}
              className="w-full p-3 border border-gray-300 rounded"
              rows="4"
            />

            <div>
              <label className="font-bold text-gray-700 block mb-1">Upload Payment Success Image</label>
              <input type="file" className="w-full text-sm text-gray-600" required />
            </div>

            <button
              type="submit"
              className="bg-teal-600 text-white py-2 px-4 rounded hover:bg-teal-700"
            >
              Submit
            </button>
          </form>
        )}
      </div>
    </div>
  );
}

export default App;
