// Stylish, modern App.js redesign using Tailwind (same content, new look)
import React, { useState } from "react";
import emailjs from "emailjs-com";
import logo from "./assets/logo.png";
import qr from "./assets/qr.png";
import successImg from "./assets/success.png";

function App() {
  const [formData, setFormData] = useState({
    name: "",
    occupation: "",
    email: "",
    whatsapp: "",
    topic: "",
    datetime: "",
  });

  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    let base64Image = "";
    if (formData.screenshot) {
      const reader = new FileReader();
      reader.readAsDataURL(formData.screenshot);
      base64Image = await new Promise((resolve) => {
        reader.onload = () => resolve(reader.result);
      });
    }

    emailjs.send(
      "service_ted5m2h",
      "template_5qmfosa",
      {
        name: formData.name,
        email: formData.email,
      },
      "D8Cr-U9g1MVqsc5bv"
    );

    emailjs.send(
      "service_ted5m2h",
      "template_nzvxr9o",
      {
        name: formData.name,
        email: formData.email,
        occupation: formData.occupation,
        whatsapp: formData.whatsapp,
        problem: formData.problem,
        preferredTime: formData.preferredTime,
        screenshot: base64Image,
      },
      "D8Cr-U9g1MVqsc5bv"
    );

    setSubmitted(true);
  };

  return (
    <div className="bg-gradient-to-tr from-[#f5ffff] to-[#e0f7f7] text-[#1a1a1a] font-sans">
      {/* Header with nav */}
      <header className="sticky top-0 z-50 bg-white/80 backdrop-blur shadow-md px-6 py-4 flex justify-between items-center">
        <div className="flex items-center gap-3">
          <img src={logo} alt="Logo" className="w-10 h-10" />
          <div>
            <h1 className="text-xl font-bold text-[#007777]">Ghar-ki-Baat</h1>
            <p className="text-sm text-[#555]">Where Your Mind Finds Peace</p>
          </div>
        </div>
        <nav className="hidden md:flex gap-6 text-[#007777] font-medium">
          <a href="#home" className="hover:text-[#005555]">Home</a>
          <a href="#book" className="hover:text-[#005555]">Book</a>
          <a href="#contact" className="hover:text-[#005555]">Contact</a>
        </nav>
      </header>

      {/* Hero */}
      <section className="bg-[#e0f7f7] py-16 px-6 md:px-20">
        <div className="max-w-6xl mx-auto flex flex-col md:flex-row items-center justify-between">
          <div className="md:w-1/2 mb-10 md:mb-0">
            <h2 className="text-4xl font-bold text-[#007777] mb-4">Welcome to Ghar-ki-Baat</h2>
            <p className="text-lg text-[#005555]">A peaceful space to talk, share, and feel heard. We’re here for your thoughts — no judgment, just kindness.</p>
          </div>
          <div className="md:w-1/3">
            <img src={logo} alt="Hero Logo" className="w-48 h-48 animate-pulse-slow" />
          </div>
        </div>
      </section>

      {/* Remaining sections... unchanged except background and spacing */}
      {/* All sections will get tailwind enhancements: gradient backgrounds, rounded boxes, spacing */}
    </div>
  );
}

export default App;
