// --- IMPORTS ---
import React, { useState } from "react";
import emailjs from "emailjs-com";
import { motion } from "framer-motion";
import logo from "./assets/logo.png";
import successImage from "./assets/success.png";
import qr from "./assets/qr.png";

// --- MAIN FUNCTION ---
function App() {
  const [formData, setFormData] = useState({ name: "", email: "", image: null });
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();

    // 1Ô∏è‚É£ Send confirmation
    emailjs
      .send(
        "service_ted5m2h",
        "template_5qmfosa",
        {
          name: formData.name,
          email: formData.email,
        },
        "D8Cr-U9g1MVqsc5bv"
      )
      .then(() => {
        console.log("Confirmation sent to user");
      });

    // 2Ô∏è‚É£ Send admin alert
    emailjs
      .send(
        "service_ted5m2h",
        "template_nzvxr9o",
        {
          name: formData.name,
          email: formData.email,
        },
        "D8Cr-U9g1MVqsc5bv"
      )
      .then(() => {
        console.log("Alert sent to admin");
      });

    setSubmitted(true);
  };

  return (
    <div className="bg-gradient-to-b from-[#e0f7f4] to-[#ffffff] text-gray-800 font-sans">
      {/* --- HEADER --- */}
      <header className="flex justify-between items-center px-6 py-4 shadow-md bg-white">
        <div>
          <h1 className="text-2xl font-bold">Ghar-ki-Baat</h1>
          <p className="text-sm text-gray-600">When no one listens, we do.</p>
        </div>
        <img src={logo} alt="Logo" className="w-16 h-16 rounded-full" />
      </header>

      {/* --- HERO --- */}
      <section className="py-12 px-6 relative bg-white">
        <div className="flex flex-col md:flex-row items-center justify-between max-w-6xl mx-auto">
          <div className="text-center md:text-left mb-6 md:mb-0">
            <motion.h2
              initial={{ y: -40, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 1 }}
              className="text-3xl font-semibold mb-2"
            >
              Ghar-ki-Baat
            </motion.h2>
            <p className="text-gray-600">When no one listens, we do.</p>
          </div>
          <img src={logo} alt="Logo" className="w-24 h-24 rounded-full" />
        </div>
      </section>

      {/* --- WHAT WE DO --- */}
      <section className="py-12 px-6 bg-[#f9ffff] text-center">
        <h3 className="text-2xl font-bold mb-4">What We Do</h3>
        <div className="max-w-2xl mx-auto space-y-4 text-gray-700">
          <p>‚Ä¢ Listen to anything on your mind ‚Äî school, life, overthinking, or just silence.</p>
          <p>‚Ä¢ Make you feel lighter with calm, honest conversation.</p>
          <p>‚Ä¢ Give simple guidance or just a space to breathe ‚Äî no pressure.</p>
        </div>
      </section>

      {/* --- OUR PLANS --- */}
      <section className="py-12 px-6 bg-[#f3fbfb] text-center">
        <h3 className="text-2xl font-bold mb-6">Our Plans</h3>
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-6 max-w-4xl mx-auto">
          {[
            {
              title: "Single Talk Session",
              duration: "35 minutes",
              validity: "1 Day",
              sessions: "1 session",
              price: "‚Çπ99",
            },
            {
              title: "One Deep Session",
              duration: "60 minutes",
              validity: "1 Day",
              sessions: "1 session",
              price: "‚Çπ149",
            },
            {
              title: "Monthly VIP Plan (Short)",
              duration: "30 min √ó 6 sessions",
              validity: "30 Days",
              sessions: "Weekly support + 2 extras",
              price: "‚Çπ700",
            },
            {
              title: "Monthly VIP Plan (Full)",
              duration: "60 min √ó 7 sessions",
              validity: "30 Days",
              sessions: "Intensive full recovery plan",
              price: "‚Çπ1500",
            },
          ].map((p, i) => (
            <div
              key={i}
              className="border rounded-xl p-6 shadow-md bg-white text-left"
            >
              <h4 className="font-bold text-lg mb-2">{p.title}</h4>
              <p>‚è± {p.duration}</p>
              <p>üìÖ {p.validity}</p>
              <p>üìç {p.sessions}</p>
              <p className="text-green-600 font-semibold mt-2">{p.price}</p>
            </div>
          ))}
        </div>
      </section>

      {/* --- WHY TRUST US --- */}
      <section className="py-12 px-6 text-center bg-white">
        <h3 className="text-2xl font-bold mb-6">Why Trust Us?</h3>
        <div className="max-w-3xl mx-auto grid grid-cols-1 md:grid-cols-3 gap-6 text-gray-700">
          <div>üõ° 100% Privacy</div>
          <div>üëÇ Compassionate Listeners</div>
          <div>üí¨ Real Talk</div>
        </div>
      </section>

      {/* --- BOOKING SECTION --- */}
      <section className="py-12 px-6" id="book">
        <h3 className="text-2xl font-bold mb-6 text-center">Book a Session</h3>
        {!submitted ? (
          <form
            onSubmit={handleSubmit}
            className="max-w-md mx-auto bg-white p-6 rounded-lg shadow"
          >
            <input
              type="text"
              placeholder="Your Name"
              className="w-full p-2 mb-4 border rounded"
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              required
            />
            <input
              type="email"
              placeholder="Your Email"
              className="w-full p-2 mb-4 border rounded"
              onChange={(e) => setFormData({ ...formData, email: e.target.value })}
              required
            />
            <label className="font-semibold block mb-2">Upload Payment Success Image:</label>
            <input
              type="file"
              className="mb-4"
              onChange={(e) =>
                setFormData({ ...formData, image: e.target.files[0] })
              }
              required
            />
            <button
              type="submit"
              className="bg-teal-600 text-white px-4 py-2 rounded hover:bg-teal-700"
            >
              Submit
            </button>
          </form>
        ) : (
          <div className="text-center">
            <h4 className="text-xl font-bold text-green-600 mt-4 mb-2">Form submitted!</h4>
            <img src={successImage} alt="Success" className="mx-auto w-32" />
          </div>
        )}
      </section>

      {/* --- QR CODE FOR PAYMENT --- */}
      <section className="py-12 px-6 text-center">
        <h3 className="text-2xl font-bold mb-4">Scan to Pay</h3>
        <img src={qr} alt="QR" className="w-48 mx-auto rounded shadow-lg" />
      </section>

      {/* --- FAQ --- */}
      <section className="py-12 px-6 bg-[#f7fffe]">
        <h3 className="text-2xl font-bold mb-6 text-center">FAQs</h3>
        <div className="max-w-2xl mx-auto space-y-4">
          <details className="bg-white p-4 rounded shadow">
            <summary className="font-semibold">Is my conversation private?</summary>
            <p className="mt-2 text-gray-600">Yes. We maintain strict confidentiality of all sessions.</p>
          </details>
          <details className="bg-white p-4 rounded shadow">
            <summary className="font-semibold">Do I need to reveal my identity?</summary>
            <p className="mt-2 text-gray-600">No, you can choose to stay anonymous if you prefer.</p>
          </details>
          <details className="bg-white p-4 rounded shadow">
            <summary className="font-semibold">What if I need help urgently?</summary>
            <p className="mt-2 text-gray-600">You can contact us via WhatsApp or email anytime, and we‚Äôll prioritize your request.</p>
          </details>
