import React, { useState } from "react";
import { FaWhatsapp } from "react-icons/fa";

function App() {
  const [formData, setFormData] = useState({
    name: "",
    age: "",
    issue: "",
    dateTime: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form Submitted:", formData);
    alert("Booking submitted successfully!");
    setFormData({ name: "", age: "", issue: "", dateTime: "" });
  };

  return (
    <div className="min-h-screen bg-gray-50 text-gray-800 p-4">
      <header className="bg-white shadow-md p-6 flex justify-between items-center rounded-xl mb-8">
        <h1 className="text-3xl font-bold">SafeCircle</h1>
        <a
          href="https://wa.me/919876543210" // replace with your WhatsApp number
          target="_blank"
          rel="noopener noreferrer"
          className="text-green-500 text-3xl hover:text-green-600"
        >
          <FaWhatsapp />
        </a>
      </header>

      <main className="max-w-xl mx-auto bg-white p-8 rounded-xl shadow-md">
        <h2 className="text-2xl font-semibold mb-6">Book a Session</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block mb-1 font-medium">Name</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              className="w-full border border-gray-300 p-2 rounded-md focus:outline-none focus:ring focus:ring-blue-200"
              required
            />
          </div>
          <div>
            <label className="block mb-1 font-medium">Age</label>
            <input
              type="number"
              name="age"
              value={formData.age}
              onChange={handleChange}
              className="w-full border border-gray-300 p-2 rounded-md focus:outline-none focus:ring focus:ring-blue-200"
              required
            />
          </div>
          <div>
            <label className="block mb-1 font-medium">Issue</label>
            <textarea
              name="issue"
              value={formData.issue}
              onChange={handleChange}
              className="w-full border border-gray-300 p-2 rounded-md focus:outline-none focus:ring focus:ring-blue-200"
              rows="3"
              required
            ></textarea>
          </div>
          <div>
            <label className="block mb-1 font-medium">Preferred Date & Time</label>
            <input
              type="datetime-local"
              name="dateTime"
              value={formData.dateTime}
              onChange={handleChange}
              className="w-full border border-gray-300 p-2 rounded-md focus:outline-none focus:ring focus:ring-blue-200"
              required
            />
          </div>
          <button
            type="submit"
            className="w-full bg-blue-500 text-white py-2 rounded-md hover:bg-blue-600 transition"
          >
            Submit
          </button>
        </form>
      </main>
    </div>
  );
}

export default App;
