import React, { useRef, useState } from "react";
import emailjs from "@emailjs/browser";

export default function App() {
  const form = useRef();
  const [submitted, setSubmitted] = useState(false);

  const sendEmail = (e) => {
    e.preventDefault();
    const formData = {
      name: form.current.name.value,
      email: form.current.email.value,
      occupation: form.current.occupation.value,
      whatsapp: form.current.whatsapp.value,
      message: form.current.message.value,
    };

    // 1️⃣ Send confirmation to USER
    emailjs.send(
      "service_ted5m2h",        // Replace with your EmailJS service ID
      "template_5qmfosa",      // Template ID for user
      {
        name: formData.name,
        email: formData.email,
      },
      "D8Cr-U9g1MVqsc5bv"         // Replace with your EmailJS public key
    ).then(() => {
      console.log("Confirmation sent to user");
    });

    // 2️⃣ Send alert to ADMIN
    emailjs.send(
      "service_ted5m2h",        // Replace with your EmailJS service ID
      "template_nzvxr9o",            // Template ID for admin
      {
        name: formData.name,
        email: formData.email,
        occupation: formData.occupation,
        whatsapp: formData.whatsapp,
        message: formData.message,
      },
      "D8Cr-U9g1MVqsc5bv"         // Replace with your EmailJS public key
    ).then(() => {
      console.log("Notification sent to admin");
      setSubmitted(true);
    });

    e.target.reset(); // Reset the form
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100 p-6">
      <h1 className="text-3xl font-bold mb-4">Book Your Session</h1>
      <form ref={form} onSubmit={sendEmail} className="bg-white p-6 rounded-lg shadow-md w-full max-w-md space-y-4">
        <input type="text" name="name" placeholder="Your Name" required className="w-full border p-2 rounded" />
        <input type="email" name="email" placeholder="Your Email" required className="w-full border p-2 rounded" />
        <input type="text" name="occupation" placeholder="Your Occupation" className="w-full border p-2 rounded" />
        <input type="text" name="whatsapp" placeholder="Your WhatsApp Number" required className="w-full border p-2 rounded" />
        <textarea name="message" placeholder="Any message..." className="w-full border p-2 rounded" />
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
          Submit
        </button>
        {submitted && <p className="text-green-600 mt-2">✔ Booking submitted. Confirmation sent!</p>}
      </form>
    </div>
  );
}
