import React, { useRef, useState } from "react";
import emailjs from "@emailjs/browser";
import "./App.css";
import logo from "./assets/logo.png";
import qr from "./assets/qr.png";

function App() {
  const form = useRef();
  const [success, setSuccess] = useState(false);

  const sendEmail = (e) => {
    e.preventDefault();
    const formData = new FormData(form.current);

    const reader = new FileReader();
    reader.onloadend = () => {
      const base64Image = reader.result;
      const formFields = Object.fromEntries(formData.entries());

      const adminTemplateParams = {
        name: formFields.user_name,
        occupation: formFields.user_occupation,
        whatsapp: formFields.user_whatsapp,
        email: formFields.user_email,
        preferred_time: formFields.user_time,
        problem: formFields.message,
        image: base64Image,
      };

      const userTemplateParams = {
        to_name: formFields.user_name,
        to_email: formFields.user_email,
      };

      emailjs.send("service_ted5m2h", "template_nzvxr9o", adminTemplateParams, "D8Cr-U9g1MVqsc5bv")
        .then(() => {
          emailjs.send("service_ted5m2h", "template_5qmfosa", userTemplateParams, "D8Cr-U9g1MVqsc5bv");
          setSuccess(true);
        });
    };

    if (formData.get("image")) {
      reader.readAsDataURL(formData.get("image"));
    }
  };

  return (
    <div className="bg-[url('./assets/stardust.png')] bg-cover text-white font-sans">
      {/* Header */}
      <header className="flex justify-between items-center p-4 border-b border-white">
        <div>
          <h1 className="text-3xl font-bold">SafeCircle</h1>
          <p className="text-sm">Confide. Connect. Heal.</p>
        </div>
        <img src={logo} alt="Logo" className="h-16" />
      </header>

      {/* Hero */}
      <section className="min-h-[50vh] flex flex-col justify-center items-center text-center p-8">
        <h2 className="text-4xl font-semibold mb-4">Your Safe Space for Mental Wellness</h2>
        <p className="max-w-xl mb-6">Book confidential sessions with certified mental health experts. Private. Trusted. Secure.</p>
        <a href="#booking" className="bg-teal-500 hover:bg-teal-600 text-white px-6 py-3 rounded-full shadow-md">Book a Session</a>
      </section>

      {/* Booking Section */}
      <section id="booking" className="py-12 px-6">
        <div className="max-w-6xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-8 items-start">
          <div>
            <h3 className="text-3xl font-semibold mb-2">Book a Private Consultation Session</h3>
            <p className="text-gray-300">Fill the form to get started with your peaceful mental health journey.</p>
          </div>

          <form ref={form} onSubmit={sendEmail} className="bg-white bg-opacity-10 p-6 rounded-lg shadow-lg">
            <div className="space-y-4">
              <input type="text" name="user_name" placeholder="Your Name" className="w-full p-2 rounded" required />
              <select name="user_occupation" className="w-full p-2 rounded" required>
                <option value="">Select Occupation</option>
                <option value="Student">Student</option>
                <option value="Professional">Professional</option>
                <option value="Other">Other</option>
              </select>
              <input type="text" name="user_whatsapp" placeholder="WhatsApp Number" className="w-full p-2 rounded" required />
              <input type="email" name="user_email" placeholder="Email ID" className="w-full p-2 rounded" required />
              <input type="text" name="user_time" placeholder="Preferred Consultation Time" className="w-full p-2 rounded" required />
              <textarea name="message" placeholder="Short description of the issue" className="w-full p-2 rounded" required></textarea>
              <input type="file" name="image" accept="image/*" className="w-full" required />
              <button type="submit" className="w-full bg-teal-600 hover:bg-teal-700 py-2 rounded">Book Session</button>
              <p className="text-xs text-gray-200 mt-1">Our team will contact you after confirmation of payment from our side.</p>
              {success && <p className="text-green-300">Session booked successfully!</p>}
            </div>
          </form>
        </div>
      </section>

      {/* QR Code */}
      <section className="py-10 text-center">
        <h4 className="text-2xl font-semibold mb-2">Scan & Pay Now</h4>
        <img src={qr} alt="QR Code" className="mx-auto h-48" />
        <p className="text-gray-300 mt-2">Use UPI or your preferred app</p>
      </section>

      {/* Why Trust Us */}
      <section className="py-10 px-6 text-center">
        <h4 className="text-2xl font-semibold mb-6">Why Trust Us?</h4>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6">
          <div>✅ 100% Privacy</div>
          <div>✅ Certified Experts</div>
          <div>✅ Confidential & Secure</div>
          <div>✅ Peaceful Support</div>
        </div>
      </section>

      {/* Pricing */}
      <section className="py-10 px-6 text-center">
        <h4 className="text-2xl font-semibold mb-6">Pricing</h4>
        <div className="max-w-md mx-auto bg-white bg-opacity-10 p-6 rounded-lg">
          <p className="text-xl font-medium mb-2">Consultation Fee: ₹499</p>
          <p className="text-sm text-gray-300">Includes 1-on-1 confidential session with expert psychologist</p>
        </div>
      </section>

      {/* Testimonials */}
      <section className="py-10 px-6">
        <h4 className="text-2xl font-semibold text-center mb-6">What Our Clients Say</h4>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="bg-white bg-opacity-10 p-4 rounded">“Truly private & comforting experience.” – Aditi</div>
          <div className="bg-white bg-opacity-10 p-4 rounded">“Easy to book, instant support. Loved it!” – Raj</div>
          <div className="bg-white bg-opacity-10 p-4 rounded">“My sessions were eye-opening and safe.” – Neha</div>
        </div>
      </section>

      {/* FAQ */}
      <section className="py-10 px-6">
        <h4 className="text-2xl font-semibold text-center mb-6">Frequently Asked Questions</h4>
        <div className="max-w-3xl mx-auto space-y-4">
          <details className="bg-white bg-opacity-10 p-4 rounded">
            <summary>Is the session really confidential?</summary>
            <p>Yes, 100%. Your details and issues are safe with us.</p>
          </details>
          <details className="bg-white bg-opacity-10 p-4 rounded">
            <summary>How long is each consultation?</summary>
            <p>Each session typically lasts 45–60 minutes.</p>
          </details>
          <details className="bg-white bg-opacity-10 p-4 rounded">
            <summary>Can I choose my preferred time?</summary>
            <p>Yes, you can suggest your preferred slot while booking.</p>
          </details>
        </div>
      </section>

      {/* Contact CTA */}
      <section className="py-10 px-6 text-center">
        <h4 className="text-2xl font-semibold mb-4">Still Have Questions?</h4>
        <p className="mb-4">Reach out to us anytime. We’re here to listen.</p>
        <div className="flex justify-center gap-6">
          <a href="mailto:support@safecircle.in" className="bg-blue-500 hover:bg-blue-600 px-4 py-2 rounded">Email Support</a>
          <a href="https://wa.me/919000000000" target="_blank" rel="noopener noreferrer" className="bg-green-500 hover:bg-green-600 px-4 py-2 rounded">WhatsApp Support</a>
        </div>
      </section>

      {/* Footer */}
      <footer className="py-6 border-t border-white text-center text-sm">
        <img src={logo} alt="Logo" className="h-10 mx-auto mb-2" />
        <p>© 2025 SafeCircle. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
