// Same imports as before
import React, { useState } from "react";
import emailjs from "emailjs-com";
import { motion } from "framer-motion";
import logo from "./assets/logo.png";
import successImage from "./assets/success.png";
import qr from "./assets/qr.png";

function App() {
  const [formData, setFormData] = useState({ name: "", email: "", image: null });
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();

    emailjs
      .send(
        "service_ted5m2h",
        "template_5qmfosa",
        {
          name: formData.name,
          email: formData.email,
        },
        "D8Cr-U9g1MVqsc5bv"
      )
      .then(() => console.log("Confirmation sent"));

    emailjs
      .send(
        "service_ted5m2h",
        "template_nzvxr9o",
        {
          name: formData.name,
          email: formData.email,
        },
        "D8Cr-U9g1MVqsc5bv"
      )
      .then(() => console.log("Alert sent to admin"));

    setSubmitted(true);
  };

  // Animation wrapper
  const FadeInSection = ({ children }) => (
    <motion.div
      initial={{ opacity: 0, y: 30 }}
      whileInView={{ opacity: 1, y: 0 }}
      viewport={{ once: true }}
      transition={{ duration: 0.6 }}
    >
      {children}
    </motion.div>
  );

  return (
    <div className="bg-gradient-to-b from-[#e6f5f2] to-white text-gray-800 font-sans scroll-smooth">
      {/* --- FLOATING HEADER --- */}
      <header className="fixed top-0 left-0 right-0 z-50 flex justify-between items-center px-6 py-4 shadow-md bg-white backdrop-blur-md bg-opacity-90">
        <div>
          <h1 className="text-2xl font-bold text-teal-700">Ghar-ki-Baat</h1>
          <p className="text-sm text-gray-600 italic">When no one listens, we do.</p>
        </div>
        <img src={logo} alt="Logo" className="w-12 h-12 rounded-full shadow" />
      </header>

      {/* Add spacing to push content below fixed header */}
      <div className="h-[90px]" />

      {/* --- HERO --- */}
      <section className="py-12 px-6 bg-gradient-to-r from-[#ecfdf5] to-white">
        <FadeInSection>
          <div className="flex flex-col md:flex-row items-center justify-between max-w-6xl mx-auto">
            <div className="text-center md:text-left mb-6 md:mb-0">
              <motion.h2
                initial={{ opacity: 0, y: -30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 1 }}
                className="text-4xl font-semibold text-teal-800 mb-3"
              >
                Ghar-ki-Baat
              </motion.h2>
              <p className="text-gray-700 text-lg">
                A space for real talk — more than advice, lighter than therapy.
              </p>
            </div>
            <img src={logo} alt="Logo" className="w-24 h-24 rounded-full shadow-md" />
          </div>
        </FadeInSection>
      </section>

      {/* Wrap the rest in FadeInSection where needed */}
      <FadeInSection>
        <section className="py-12 px-6 bg-white text-center">
          <h3 className="text-2xl font-bold text-teal-700 mb-4">What We Do</h3>
          <div className="max-w-2xl mx-auto space-y-4 text-gray-700 text-md">
            <p>• Listen to anything on your mind — stress, career confusion, relationships, dilemmas, or silence.</p>
            <p>• Make you feel lighter with calm, honest conversations.</p>
            <p>• Offer simple guidance or just space to breathe — no pressure.</p>
          </div>
        </section>
      </FadeInSection>

      {/* Same for Our Plans */}
      <FadeInSection>
        <section className="py-12 px-6 bg-[#f3fbfb] text-center">
          <h3 className="text-2xl font-bold text-teal-700 mb-6">Our Plans</h3>
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-6 max-w-4xl mx-auto">
            {/* Same pricing cards as before */}            
          </div>
        </section>
      </FadeInSection>

      {/* WHY TRUST US */}
      <FadeInSection>
        <section className="py-12 px-6 bg-white text-center">
          <h3 className="text-2xl font-bold text-teal-700 mb-6">Why Trust Us?</h3>
          <div className="max-w-3xl mx-auto grid grid-cols-1 md:grid-cols-3 gap-6 text-gray-700 text-lg">
            <div>🛡 100% Privacy</div>
            <div>👂 Compassionate Listeners</div>
            <div>💬 Real Talk</div>
          </div>
        </section>
      </FadeInSection>

      {/* BOOKING SECTION */}
      <FadeInSection>
        <section className="py-12 px-6 bg-[#f9fffe]" id="book">
          <h3 className="text-2xl font-bold text-center text-teal-700 mb-6">Book a Session</h3>
          <div className="flex flex-col md:flex-row gap-8 justify-center items-start">
            {!submitted ? (
              <form onSubmit={handleSubmit} className="w-full md:w-1/2 bg-white p-6 rounded-xl shadow-md space-y-4">
                {/* Input fields same as before */}
              </form>
            ) : (
              <motion.div
                className="text-center w-full md:w-1/2"
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.5 }}
              >
                <h4 className="text-xl font-bold text-green-600 mb-4">Form submitted!</h4>
                <img src={successImage} alt="Success" className="mx-auto w-32" />
              </motion.div>
            )}

            {/* QR Code */}
            <div className="w-full md:w-1/3 text-center">
              <h4 className="text-xl font-semibold mb-2 text-teal-700">Scan to Pay</h4>
              <img src={qr} alt="QR Code" className="w-48 mx-auto rounded-xl shadow-lg" />
            </div>
          </div>
        </section>
      </FadeInSection>

      {/* Repeat FadeInSection for Testimonials, FAQ, CTA and Footer */}

      {/* Footer */}
      <footer className="bg-gray-800 text-white text-center py-4 mt-12">
        <p>&copy; {new Date().getFullYear()} Ghar-ki-Baat. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
